    overflow: hidden;
    z-index: 1;
    transition: transform 0.2s;
}

.schedule-event:hover {
    transform: scale(1.02);
    z-index: 2;
}

.schedule-event-title {
    font-weight: 600;
    margin-bottom: 2px;
}

.schedule-event-time {
    font-size: 10px;
    opacity: 0.9;
}

.event-filters {
    display: flex;
    gap: 12px;
    margin-bottom: 20px;
}

.filter-btn {
    padding: 12px 24px;
    border-radius: 8px;
    border: none;
    background: #4a5ba6;
    color: white;
    cursor: pointer;
    font-size: 13px;
    font-weight: 500;
    transition: all 0.3s;
}

.filter-btn:hover {
    opacity: 0.9;
}

.filter-btn.active {
    background: #5d6db8;
}

.action-buttons {
    display: flex;
    gap: 12px;
    justify-content: flex-end;
}

.save-btn,
.update-btn {
    padding: 12px 24px;
    border-radius: 24px;
    border: none;
    cursor: pointer;
    font-size: 13px;
    font-weight: 600;
    transition: all 0.3s;
}

.save-btn {
    background: #f0f0f0;
    color: #333;
}

.update-btn {
    background: #4a5ba6;
    color: white;
}

.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 1000;
    align-items: center;
    justify-content: center;
}

.modal.active {
    display: flex;
}

.modal-content {
    background: white;
    border-radius: 16px;
    padding: 24px;
    width: 90%;
    max-width: 500px;
    max-height: 90vh;
    overflow-y: auto;
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 24px;
}

.modal-header h2 {
    font-size: 20px;
    font-weight: 600;
}

.close-btn {
    background: none;
    border: none;
    font-size: 28px;
    cursor: pointer;
    color: #666;
}

.form-group {
    margin-bottom: 16px;
}

.form-group label {
    display: block;
    font-size: 13px;
    font-weight: 600;
    margin-bottom: 6px;
    color: #333;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 10px 12px;
    border: 1px solid #e0e0e0;
    border-radius: 8px;
    font-size: 14px;
    font-family: inherit;
}

.form-row {
    display: flex;
    gap: 12px;
}

.form-row .form-group {
    flex: 1;
}

.modal-actions {
    display: flex;
    gap: 12px;
    justify-content: flex-end;
    margin-top: 24px;
}

.cancel-btn,
.submit-btn {
    padding: 10px 24px;
    border-radius: 8px;
    border: none;
    cursor: pointer;
    font-size: 14px;
    font-weight: 600;
    transition: all 0.3s;
}

.cancel-btn {
    background: #f0f0f0;
    color: #333;
}

.submit-btn {
    background: #4a5ba6;
    color: white;
}

.submit-btn:hover {
    background: #5d6db8;
}

@media (max-width: 1200px) {
    .container {
        flex-direction: column;
    }

    .sidebar {
        width: 100%;
    }

    .schedule-grid {
        overflow-x: auto;
    }
}

